<style>
    .topbar {
        background-color: #4a25aa;
        overflow:auto;
        display: flex;
        width: auto;
        justify-content: space-between;
        align-items: center;
        position: fixed;
        width: 100%;
        z-index: 4;
    }

    .topbar a {
        justify-content: center;
        align-items: center;
        display: flex;
        color: white;
        text-align: center;
        padding: 12px 24px;
        text-decoration: none;
        flex: 1;
        gap: 8px;
    }

    .topbar a:hover, .topbar a.active  {
        background: var( --Semantic-Main, linear-gradient(270deg, #4a25aa 0%, #b60f7f 50%, #4a25aa 100%));
        color: white;
    }
</style>

